function cleanId(id) {
  id = id.replace(/\s+/g, '-');
  id = id.replace(/[^\w\s-]/gi, '');

  if (!/^[a-zA-Z_]/.test(id)) {
    id = '_' + id;
  }

  return id.toLowerCase();
}

function esmshCss(file) {
  const id = cleanId(file)

  const found = document.getElementById(id)
  if (found) { return found }

  const link = document.createElement('link')
  link.id = id
  link.href = 'https://esm.sh/' + file
  link.rel = 'stylesheet'

  document.head.appendChild(link)

  return link
}

function formatDuration(duration) {
  const hours = Math.trunc(duration)
  const minutes = Math.round(duration * 60 % 60)
  return `${hours}:${String(minutes).padStart(2, '0')}`
}

function labeler(...labels) {
  return values => Object.fromEntries(lodash.zip(labels, values))
}

function roundOn(num, step) {
  if (step < 0) {
    return Math.round(num * step) / step
  }
  else if (step < 1) {
    const stepInv = 1 / step
    const stepRound = Math.round(stepInv)
    const stepClean = Math.abs(stepRound - stepInv) < 1e-10 ? stepRound : stepInv
    return Math.round(num * stepClean) / stepClean
  }
  else {
    return step * Math.round(num / step)
  }
}

