function update(value, path, fn) {
  if (!path || Array.isArray(path) && path.length === 0) {
    return fn(value)
  }
  else {
    return immutable.update(value, path, fn)
  }
}

function SimpleBlock(init, view, desc={}) {
  return resheet.core.block.create({
    init,
    view({ state, dispatch, env }, ref) {
      function setState(newState, path=[]) {
        const updateState = (
          typeof newState === 'function' ?
            newState
          :
            () => newState
        )

        dispatch(state => ({
          state: update(state, path, updateState),
        }))
      }

      function onUpdate(evPath, statePath=[], processValue=lodash.identity) {
        return ev => {
          setState(processValue(lodash.get(ev, evPath)), statePath)
        }
      }

      function fieldDispatcher(field) {
        return resheet.util.dispatch.fieldDispatcher(field, dispatch)
      }

      return view({
        state, dispatch, env, ref,
        setState, onUpdate, fieldDispatcher,
      })
    },
    getResult(state) { return state },
    recompute(state) { return { state, invalidated: false } },
    fromJSON(json) { return json ?? init },
    toJSON(state) { return state },
    ...desc,
  })
}

function Select(values) {
  return SimpleBlock(values[0],
    ({ ref, state, onUpdate }) => (
      <select
        ref={ref}
        value={state}
        onChange={onUpdate('target.value')}
      >
        {values.map(value => (
          <option key={value} value={value}>
            {value}
          </option>
        ))}
      </select>
    )
  )
}

function DateBlock(init) {
  return SimpleBlock(init ?? new Date(0),
    ({ ref, state, setState }) => {
      const divRef = React.useRef()

      function focus() {
        divRef.current
          ?.querySelector('.react-datepicker [tabindex="0"]')
          ?.focus()
      }

      React.useImperativeHandle(ref, () => ({ focus }), [])

      return (
        <div
          ref={divRef}
          className="flex flex-col items-center"
          onKeyDown={ev => {
            if (ev.key !== 'Escape') {
              ev.stopPropagation()
             setTimeout(focus)
            }
          }}
        >
          <libs.Datepicker
            ref={ref}
            inline
            selected={state}
            onSelect={date => setState(date)}
          >
            <button
              className="w-full py-1 hover:bg-blue-50"
              onClick={() => setState(new Date())}
            >
              Today
            </button>
          </libs.Datepicker>
        </div>
      )
    },
    {
      toJSON(date) { return libs.datefns.formatISO(date) },
      fromJSON(json) { return libs.datefns.parseISO(json) },
    },
  )
}

